import { Component } from '@angular/core';

@Component({
  selector: 'patient-ui',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'hospitalproject';
  name: string = "";
  age: number = 0;
  CallMe() {
    alert("My name is " + this.name + ". " + "I am " + this.age + "years old.");
  }
}
